import React, { Component } from 'react';
import Amplify, { graphqlOperation }  from "aws-amplify";
import { Connect } from "aws-amplify-react";

import * as queries from './graphql/queries';
import * as subscriptions from './graphql/subscriptions';

class App extends Component {

    render() {

        const ListView = ({ todos }) => (
            <div>
                <h3>All Todos</h3>
                <ul>
                    {todos.map(todo => <li key={todo.id}>{todo.name} ({todo.id})</li>)}
                </ul>
            </div>
        );

        return (
            <Connect query={graphqlOperation(queries.listMyTypes)}>
                {({ data: { listMyType }, loading, errors }) => {
                    if (errors) return (<h3>Error</h3>);
                    if (loading || !listMyType) return (<h3>Loading...</h3>);
                    return (<ListView todos={listMyType.items} /> );
                }}
            </Connect>
        )
    }
} 

export default App;
